mapper.select:
           select  t.operate,t.intro,t.remark,t.commodity_id,t.commodity_id commodityId,t.intro_id,t.intro_id introId,t.b_id,t.b_id bId,t.user_id,t.user_id userId 
from business_commodity_intro t 
where 1 =1 

   and t.operate= #{operate}


   and t.intro= #{intro}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.intro_id= #{introId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

mapper.insert:
        insert into commodity_intro(
intro,remark,commodity_id,intro_id,b_id,user_id
) select t.intro,t.remark,t.commodity_id,t.intro_id,t.b_id,t.user_id from business_commodity_intro t where 1=1
   and t.operate= 'ADD'

   and t.intro= #{intro}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.intro_id= #{introId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

mapper.select:
        select  t.intro,t.remark,t.commodity_id,t.commodity_id commodityId,t.intro_id,t.intro_id introId,t.b_id,t.b_id bId,t.user_id,t.user_id userId 
from commodity_intro t 
where 1 =1 

   and t.intro= #{intro}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.intro_id= #{introId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  commodity_intro t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.intro= #{intro}


, t.remark= #{remark}


, t.commodity_id= #{commodityId}


, t.user_id= #{userId}

 where 1=1 
and t.intro_id= #{introId}


and t.b_id= #{bId}

mapper.select:
        select  count(1) count 
from commodity_intro t 
where 1 =1 

   and t.intro= #{intro}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.intro_id= #{introId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}
