mapper.select:
           select  t.operate,t.original_price,t.original_price originalPrice,t.show,t.current_price,t.current_price currentPrice,t.remark,t.commodity_id,t.commodity_id commodityId,t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.title,t.user_id,t.user_id userId 
from business_commodity t 
where 1 =1 

   and t.operate= #{operate}


   and t.original_price= #{originalPrice}


   and t.show= #{show}


   and t.current_price= #{currentPrice}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.community_id= #{communityId}


   and t.b_id= #{bId}


   and t.title= #{title}


   and t.user_id= #{userId}

mapper.insert:
        insert into m_commodity(
original_price,show,current_price,remark,commodity_id,community_id,b_id,title,user_id
) select t.original_price,t.show,t.current_price,t.remark,t.commodity_id,t.community_id,t.b_id,t.title,t.user_id from business_commodity t where 1=1
   and t.operate= 'ADD'

   and t.original_price= #{originalPrice}


   and t.show= #{show}


   and t.current_price= #{currentPrice}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.community_id= #{communityId}


   and t.b_id= #{bId}


   and t.title= #{title}


   and t.user_id= #{userId}

mapper.select:
        select  t.original_price,t.original_price originalPrice,t.show,t.current_price,t.current_price currentPrice,t.remark,t.commodity_id,t.commodity_id commodityId,t.community_id,t.community_id communityId,t.b_id,t.b_id bId,t.title,t.user_id,t.user_id userId 
from m_commodity t 
where 1 =1 

   and t.original_price= #{originalPrice}


   and t.show= #{show}


   and t.current_price= #{currentPrice}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.community_id= #{communityId}


   and t.b_id= #{bId}


   and t.title= #{title}


   and t.user_id= #{userId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  m_commodity t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.original_price= #{originalPrice}


, t.show= #{show}


, t.current_price= #{currentPrice}


, t.remark= #{remark}


, t.community_id= #{communityId}


, t.title= #{title}


, t.user_id= #{userId}

 where 1=1 
and t.commodity_id= #{commodityId}


and t.b_id= #{bId}

mapper.select:
        select  count(1) count 
from m_commodity t 
where 1 =1 

   and t.original_price= #{originalPrice}


   and t.show= #{show}


   and t.current_price= #{currentPrice}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.community_id= #{communityId}


   and t.b_id= #{bId}


