mapper.select:
           select  t.operate,t.photo_id,t.photo_id photoId,t.photo,t.remark,t.commodity_id,t.commodity_id commodityId,t.b_id,t.b_id bId,t.user_id,t.user_id userId 
from business_commodity_photo t 
where 1 =1 

   and t.operate= #{operate}


   and t.photo_id= #{photoId}


   and t.photo= #{photo}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

mapper.insert:
        insert into commodity_photo(
photo_id,photo,remark,commodity_id,b_id,user_id
) select t.photo_id,t.photo,t.remark,t.commodity_id,t.b_id,t.user_id from business_commodity_photo t where 1=1
   and t.operate= 'ADD'

   and t.photo_id= #{photoId}


   and t.photo= #{photo}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

mapper.select:
        select  t.photo_id,t.photo_id photoId,t.photo,t.remark,t.commodity_id,t.commodity_id commodityId,t.b_id,t.b_id bId,t.user_id,t.user_id userId 
from commodity_photo t 
where 1 =1 

   and t.photo_id= #{photoId}


   and t.photo= #{photo}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  commodity_photo t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.photo= #{photo}


, t.remark= #{remark}


, t.commodity_id= #{commodityId}


, t.user_id= #{userId}

 where 1=1 
and t.photo_id= #{photoId}


and t.b_id= #{bId}

mapper.select:
        select  count(1) count 
from commodity_photo t 
where 1 =1 

   and t.photo_id= #{photoId}


   and t.photo= #{photo}


   and t.remark= #{remark}


   and t.commodity_id= #{commodityId}


   and t.b_id= #{bId}


