mapper.select:
           select  t.operate,t.company_floor,t.company_floor companyFloor,t.company_name,t.company_name companyName,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.user_id,t.user_id userId,t.address_id,t.address_id addressId,t.member_id,t.member_id memberId 
from business_building_owner_delivery_address t 
where 1 =1 

   and t.operate= #{operate}


   and t.company_floor= #{companyFloor}


   and t.company_name= #{companyName}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.address_id= #{addressId}


   and t.member_id= #{memberId}

mapper.insert:
        insert into building_owner_delivery_address(
company_floor,company_name,owner_id,b_id,user_id,address_id,member_id
) select t.company_floor,t.company_name,t.owner_id,t.b_id,t.user_id,t.address_id,t.member_id from business_building_owner_delivery_address t where 1=1
   and t.operate= 'ADD'

   and t.company_floor= #{companyFloor}


   and t.company_name= #{companyName}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.address_id= #{addressId}


   and t.member_id= #{memberId}

mapper.select:
        select  t.company_floor,t.company_floor companyFloor,t.company_name,t.company_name companyName,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.user_id,t.user_id userId,t.address_id,t.address_id addressId,t.member_id,t.member_id memberId 
from building_owner_delivery_address t 
where 1 =1 

   and t.company_floor= #{companyFloor}


   and t.company_name= #{companyName}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.address_id= #{addressId}


   and t.member_id= #{memberId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  building_owner_delivery_address t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.company_floor= #{companyFloor}


, t.company_name= #{companyName}


, t.owner_id= #{ownerId}


, t.user_id= #{userId}


, t.member_id= #{memberId}

 where 1=1 
and t.b_id= #{bId}


and t.address_id= #{addressId}

mapper.select:
